import React, { useState, useRef } from 'react';
import './Modal.css';

function DraggableModal({ children, onClose, position = { x: 50, y: 50 } }) {
  const [isEditMode, setIsEditMode] = useState(false);
  const [modalPosition, setModalPosition] = useState(position);
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const modalRef = useRef(null);

  const handleMouseDown = (e) => {
    if (e.target.closest('.modal-content')) return; // Don't start drag if clicking content
    setIsDragging(true);
    setDragStart({
      x: e.clientX - modalPosition.x,
      y: e.clientY - modalPosition.y
    });
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    
    setModalPosition({
      x: e.clientX - dragStart.x,
      y: e.clientY - dragStart.y
    });
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleDetach = () => {
    const modalFeatures = 'width=600,height=600,left=200,top=200';
    const newWindow = window.open('', '_blank', modalFeatures);
    
    newWindow.document.write(`
      <html>
        <head>
          <title>Content Details</title>
          <link rel="stylesheet" href="${window.location.origin}/modal.css">
          ${document.head.innerHTML}
        </head>
        <body>
          <div id="modal-root"></div>
        </body>
      </html>
    `);

    const modalContent = modalRef.current.querySelector('.modal-content').cloneNode(true);
    newWindow.document.getElementById('modal-root').appendChild(modalContent);
    onClose();
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div
        ref={modalRef}
        className="modal"
        style={{
          left: modalPosition.x,
          top: modalPosition.y,
          cursor: isDragging ? 'grabbing' : 'grab'
        }}
        onClick={e => e.stopPropagation()}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        <div className="modal-header">
          <div className="modal-controls">
            <button 
              className="modal-control-button"
              onClick={() => setIsEditMode(!isEditMode)}
              title={isEditMode ? "Save" : "Edit"}
            >
              <i className={`fas fa-${isEditMode ? 'save' : 'edit'}`}></i>
            </button>
            <button 
              className="modal-control-button"
              onClick={handleDetach}
              title="Open in new window"
            >
              <i className="fas fa-external-link-alt"></i>
            </button>
            <button 
              className="modal-control-button"
              onClick={onClose}
              title="Close"
            >
              <i className="fas fa-times"></i>
            </button>
          </div>
        </div>
        <div className="modal-content">
          {React.Children.map(children, child =>
            React.cloneElement(child, { isEditMode })
          )}
        </div>
      </div>
    </div>
  );
}

export default DraggableModal;
