import React, { useState, useEffect } from 'react';
import Calendar from './components/Calendar';
import AddItemModal from './components/AddItemModal';
import CampaignModal from './components/CampaignModal';
import SocialPostModal from './components/SocialPostModal';
import DraggableModal from './components/DraggableModal';
import './App.css';

function App() {
  const [showModal, setShowModal] = useState(false);
  const [contentItems, setContentItems] = useState([]);
  const [campaigns, setCampaigns] = useState([]);
  const [socialPosts, setSocialPosts] = useState([]);
  const [editingContentItem, setEditingContentItem] = useState(null);
  const [showDropdown, setShowDropdown] = useState(false);
  const [itemType, setItemType] = useState(null);
  const [showSettingsDropdown, setShowSettingsDropdown] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [selectedContentItem, setSelectedContentItem] = useState(null);
  const [selectedCampaign, setSelectedCampaign] = useState(null);
  const [selectedSocialPost, setSelectedSocialPost] = useState(null);

  const itemTypes = [
    { 
      id: 'content', 
      label: 'Content Item',
      color: '#1a75ff'
    },
    { 
      id: 'campaign', 
      label: 'Campaign',
      color: '#FFB3BA'
    },
    { 
      id: 'social', 
      label: 'Social Post',
      color: '#1DA1F2'
    }
  ];

  useEffect(() => {
    fetchContentItems();
    fetchCampaigns();
    fetchSocialPosts();
  }, []);

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', isDarkMode ? 'dark' : 'light');
  }, [isDarkMode]);

  const fetchContentItems = async () => {
    try {
      const response = await fetch('/api/content-items');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setContentItems(data);
    } catch (error) {
      console.error('Error fetching content items:', error);
    }
  };

  const fetchCampaigns = async () => {
    try {
      const response = await fetch('/api/campaigns');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setCampaigns(data);
    } catch (error) {
      console.error('Error fetching campaigns:', error);
      setCampaigns([]);
    }
  };

  const fetchSocialPosts = async () => {
    try {
      const response = await fetch('/api/social-posts');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setSocialPosts(data);
    } catch (error) {
      console.error('Error fetching social posts:', error);
      setSocialPosts([]);
    }
  };

  const handleAddContentItem = async (newContentItem) => {
    try {
      const response = await fetch('/api/content-items', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newContentItem),
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const savedContentItem = await response.json();
      setContentItems([...contentItems, savedContentItem]);
      setShowModal(false);
    } catch (error) {
      console.error('Error adding content item:', error);
      throw error;
    }
  };

  const handleUpdateContentItem = async (updatedContentItem) => {
    try {
      const response = await fetch(`/api/content-items/${updatedContentItem.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedContentItem),
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const savedContentItem = await response.json();
      setContentItems(contentItems.map(item => 
        item.id === savedContentItem.id ? savedContentItem : item
      ));
      setShowModal(false);
    } catch (error) {
      console.error('Error updating content item:', error);
      throw error;
    }
  };

  const handleSaveContentItem = async (contentItem) => {
    if (contentItem.id) {
      await handleUpdateContentItem(contentItem);
    } else {
      await handleAddContentItem(contentItem);
    }
  };

  const handleEditItem = (type, item) => {
    switch (type) {
      case 'content':
        setSelectedContentItem(item);
        break;
      case 'campaign':
        setSelectedCampaign(item);
        break;
      case 'social':
        setSelectedSocialPost(item);
        break;
      default:
        break;
    }
  };

  const handleCloseModal = () => {
    setEditingContentItem(null);
    setItemType(null);
    setShowModal(false);
  };

  const handleDeleteContentItem = async (id) => {
    try {
      const response = await fetch(`/api/content-items/${id}`, {
        method: 'DELETE',
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      setContentItems(contentItems.filter(item => item.id !== id));
      setSelectedContentItem(null);
    } catch (error) {
      console.error('Error deleting content item:', error);
    }
  };

  const handleAddClick = () => {
    setShowDropdown(!showDropdown);
  };

  const handleItemTypeSelect = (type) => {
    setItemType(type.id);
    setShowDropdown(false);
    setShowModal(true);
  };

  const handleSaveCampaign = async (campaign) => {
    try {
      const method = campaign.id ? 'PUT' : 'POST';
      const url = campaign.id ? `/api/campaigns/${campaign.id}` : '/api/campaigns';
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(campaign),
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const savedCampaign = await response.json();
      
      if (campaign.id) {
        setCampaigns(campaigns.map(c => 
          c.id === savedCampaign.id ? savedCampaign : c
        ));
      } else {
        setCampaigns([...campaigns, savedCampaign]);
      }
      setShowModal(false);
    } catch (error) {
      console.error('Error saving campaign:', error);
      throw error;
    }
  };

  const handleDeleteCampaign = async (id) => {
    try {
      const response = await fetch(`/api/campaigns/${id}`, {
        method: 'DELETE',
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      setCampaigns(campaigns.filter(campaign => campaign.id !== id));
      setSelectedCampaign(null);
    } catch (error) {
      console.error('Error deleting campaign:', error);
    }
  };

  const handleSaveSocialPost = async (post) => {
    try {
      const method = post.id ? 'PUT' : 'POST';
      const url = post.id ? `/api/social-posts/${post.id}` : '/api/social-posts';
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(post),
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const savedPost = await response.json();
      
      if (post.id) {
        setSocialPosts(socialPosts.map(p => 
          p.id === savedPost.id ? savedPost : p
        ));
      } else {
        setSocialPosts([...socialPosts, savedPost]);
      }
      setShowModal(false);
    } catch (error) {
      console.error('Error saving social post:', error);
      throw error;
    }
  };

  const handleDeleteSocialPost = async (id) => {
    try {
      const response = await fetch(`/api/social-posts/${id}`, {
        method: 'DELETE',
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      setSocialPosts(socialPosts.filter(post => post.id !== id));
      setSelectedSocialPost(null);
    } catch (error) {
      console.error('Error deleting social post:', error);
    }
  };

  const handleThemeToggle = () => {
    setIsDarkMode(!isDarkMode);
  };

  const handleMoveItem = async (type, itemId, newDate) => {
    try {
      let endpoint;
      let updateFunction;
      let items;

      switch (type) {
        case 'content':
          endpoint = '/api/content-items';
          updateFunction = setContentItems;
          items = contentItems;
          break;
        case 'social':
          endpoint = '/api/social-posts';
          updateFunction = setSocialPosts;
          items = socialPosts;
          break;
        default:
          return;
      }

      const item = items.find(i => i.id === itemId);
      if (!item) return;

      const updatedItem = { ...item, date: newDate };

      const response = await fetch(`${endpoint}/${itemId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedItem),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const savedItem = await response.json();
      updateFunction(items.map(i => i.id === savedItem.id ? savedItem : i));
    } catch (error) {
      console.error('Error moving item:', error);
    }
  };

  return (
    <div className="App">
      <div className="header">
        <h1>Content Calendar</h1>
        <div className="header-controls">
          <div className="settings-container">
            <button 
              onClick={() => setShowSettingsDropdown(!showSettingsDropdown)} 
              className="settings-button"
            >
              <i className="fas fa-ellipsis-v"></i>
            </button>
            {showSettingsDropdown && (
              <div className="settings-dropdown">
                <div className="settings-item">
                  <span>Color Scheme</span>
                  <label className="switch">
                    <input
                      type="checkbox"
                      checked={isDarkMode}
                      onChange={handleThemeToggle}
                    />
                    <span className="slider">
                      <span className="slider-text">
                        {isDarkMode ? 'Dark' : 'Light'}
                      </span>
                    </span>
                  </label>
                </div>
              </div>
            )}
          </div>
          <div className="add-button-container">
            <button onClick={handleAddClick} className="add-button">
              Add New {showDropdown ? '▼' : '▶'}
            </button>
            {showDropdown && (
              <div className="add-dropdown">
                {itemTypes.map(type => (
                  <button
                    key={type.id}
                    className="dropdown-item"
                    onClick={() => handleItemTypeSelect(type)}
                  >
                    {type.label}
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
      <Calendar 
        contentItems={contentItems} 
        campaigns={campaigns}
        socialPosts={socialPosts}
        onEditItem={handleEditItem}
        onMoveItem={handleMoveItem}
      />
      {selectedContentItem && (
        <DraggableModal onClose={() => setSelectedContentItem(null)}>
          <AddItemModal
            contentItem={selectedContentItem}
            campaigns={campaigns}
            onSave={handleSaveContentItem}
            onCancel={() => setSelectedContentItem(null)}
            onDelete={handleDeleteContentItem}
          />
        </DraggableModal>
      )}

      {selectedCampaign && (
        <DraggableModal onClose={() => setSelectedCampaign(null)}>
          <CampaignModal
            campaign={selectedCampaign}
            onSave={handleSaveCampaign}
            onCancel={() => setSelectedCampaign(null)}
            onDelete={handleDeleteCampaign}
          />
        </DraggableModal>
      )}

      {selectedSocialPost && (
        <DraggableModal onClose={() => setSelectedSocialPost(null)}>
          <SocialPostModal
            post={selectedSocialPost}
            onSave={handleSaveSocialPost}
            onCancel={() => setSelectedSocialPost(null)}
            onDelete={handleDeleteSocialPost}
          />
        </DraggableModal>
      )}
    </div>
  );
}

export default App;
