import React, { useState } from 'react';
import './Modal.css';

const socialPlatforms = [
  { id: 'twitter', name: 'Twitter', color: '#1DA1F2', icon: 'fab fa-twitter' },
  { id: 'facebook', name: 'Facebook', color: '#4267B2', icon: 'fab fa-facebook' },
  { id: 'instagram', name: 'Instagram', color: '#E1306C', icon: 'fab fa-instagram' },
  { id: 'linkedin', name: 'LinkedIn', color: '#0077B5', icon: 'fab fa-linkedin' }
];

function SocialPostModal({ post, onSave, onCancel, onDelete }) {
  const isEditMode = !!post;

  const [formData, setFormData] = useState({
    title: post?.title || '',
    message: post?.message || '',
    platforms: post?.platforms || [],
    date: post?.date || new Date().toISOString().split('T')[0],
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await onSave(formData);
    } catch (err) {
      console.error('Failed to save social post:', err);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePlatformChange = (e) => {
    const platform = e.target.value;
    const newPlatforms = e.target.checked
      ? [...formData.platforms, platform]
      : formData.platforms.filter(p => p !== platform);
    
    setFormData({
      ...formData,
      platforms: newPlatforms
    });
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2>{isEditMode ? 'Update Social Post' : 'Add New Social Post'}</h2>
          {isEditMode && (
            <button 
              className="delete-button"
              onClick={() => onDelete(post.id)}
              title="Delete Social Post"
            >
              <i className="fas fa-trash"></i>
            </button>
          )}
        </div>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Title: <span className="required">*</span></label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Message: <span className="required">*</span></label>
            <textarea
              name="message"
              value={formData.message}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Platforms: <span className="required">*</span></label>
            <div className="platforms-select">
              {socialPlatforms.map(platform => (
                <label key={platform.id} className="platform-option">
                  <input
                    type="checkbox"
                    name="platforms"
                    value={platform.id}
                    checked={formData.platforms.includes(platform.id)}
                    onChange={handlePlatformChange}
                  />
                  <i className={platform.icon} style={{ color: platform.color }}></i>
                  {platform.name}
                </label>
              ))}
            </div>
          </div>
          <div className="form-group">
            <label>Date: <span className="required">*</span></label>
            <input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              required
            />
          </div>
          <div className="modal-buttons">
            <button 
              type="button" 
              onClick={onCancel}
            >
              Cancel
            </button>
            <button 
              type="submit"
            >
              {isEditMode ? 'Update' : 'Save'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default SocialPostModal;
