import React, { useState } from 'react';
import './Modal.css';

const socialPlatforms = [
  { 
    id: 'twitter', 
    name: 'Twitter', 
    color: '#1DA1F2', 
    icon: 'fab fa-twitter',
    tooltip: 'Share on Twitter'
  },
  { 
    id: 'facebook', 
    name: 'Facebook', 
    color: '#4267B2', 
    icon: 'fab fa-facebook',
    tooltip: 'Post on Facebook'
  },
  { 
    id: 'instagram', 
    name: 'Instagram', 
    color: '#E1306C', 
    icon: 'fab fa-instagram',
    tooltip: 'Share on Instagram'
  },
  { 
    id: 'linkedin', 
    name: 'LinkedIn', 
    color: '#0077B5', 
    icon: 'fab fa-linkedin',
    tooltip: 'Post on LinkedIn'
  }
];

function SocialPostModal({ post, onSave, onCancel, onDelete, isEditMode = false }) {
  const [formData, setFormData] = useState({
    title: post?.title || '',
    message: post?.message || '',
    platforms: post?.platforms || [],
    date: post?.date || new Date().toISOString().split('T')[0],
  });
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Validate required fields
      if (!formData.title.trim()) {
        throw new Error('Title is required');
      }
      if (!formData.message.trim()) {
        throw new Error('Message is required');
      }
      if (formData.platforms.length === 0) {
        throw new Error('Please select at least one platform');
      }

      // Validate platforms
      const invalidPlatforms = formData.platforms.filter(
        platformId => !socialPlatforms.find(p => p.id === platformId)
      );
      if (invalidPlatforms.length > 0) {
        throw new Error(`Invalid platform(s): ${invalidPlatforms.join(', ')}`);
      }

      await onSave(formData);
    } catch (err) {
      setError(err.message || 'Failed to save social post');
      console.error('Error saving social post:', err);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePlatformChange = (e) => {
    const platform = e.target.value;
    const newPlatforms = e.target.checked
      ? [...formData.platforms, platform]
      : formData.platforms.filter(p => p !== platform);
    
    setFormData({
      ...formData,
      platforms: newPlatforms
    });
    setError(''); // Clear error when selection changes
  };

  if (!isEditMode) {
    return (
      <div className="read-mode">
        <div className="form-group">
          <div className="form-label">Title</div>
          <div className="form-value">{formData.title}</div>
        </div>
        <div className="form-group">
          <div className="form-label">Message</div>
          <div className="form-value">{formData.message}</div>
        </div>
        <div className="form-group">
          <div className="form-label">Platforms</div>
          <div className="form-value">
            <div className="social-icons">
              {formData.platforms.map(platformId => {
                const platform = socialPlatforms.find(p => p.id === platformId);
                if (!platform) return null;
                return (
                  <i 
                    key={`icon-${platformId}`}
                    className={platform.icon}
                    style={{ color: platform.color }}
                    title={platform.tooltip}
                  />
                );
              })}
            </div>
          </div>
        </div>
        <div className="form-group">
          <div className="form-label">Date</div>
          <div className="form-value">{formData.date}</div>
        </div>
      </div>
    );
  }

  return (
    <form onSubmit={handleSubmit}>
      <div className="modal-header">
        <h2>{isEditMode ? 'Update Social Post' : 'Add New Social Post'}</h2>
        {isEditMode && (
          <button 
            className="delete-button"
            onClick={() => onDelete(post.id)}
            title="Delete Social Post"
          >
            <i className="fas fa-trash"></i>
          </button>
        )}
      </div>
      <div className="form-group">
        <label>Title: <span className="required">*</span></label>
        <input
          type="text"
          name="title"
          value={formData.title}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Message: <span className="required">*</span></label>
        <textarea
          name="message"
          value={formData.message}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Platforms: <span className="required">*</span></label>
        <div className="platforms-select">
          {socialPlatforms.map(platform => (
            <label 
              key={`platform-${platform.id}`} 
              className={`platform-option ${formData.platforms.includes(platform.id) ? 'selected' : ''}`}
              title={platform.tooltip}
            >
              <input
                type="checkbox"
                name="platforms"
                value={platform.id}
                checked={formData.platforms.includes(platform.id)}
                onChange={handlePlatformChange}
              />
              <i 
                className={platform.icon} 
                style={{ color: platform.color }}
              />
              <span className="platform-name">{platform.name}</span>
              {formData.platforms.includes(platform.id) && (
                <i className="fas fa-check check-icon"></i>
              )}
            </label>
          ))}
        </div>
      </div>
      <div className="form-group">
        <label>Date: <span className="required">*</span></label>
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          required
        />
      </div>
      {error && <div className="error-message">{error}</div>}
      <div className="modal-buttons">
        <button 
          type="button" 
          onClick={onCancel}
        >
          Cancel
        </button>
        <button 
          type="submit"
        >
          {isEditMode ? 'Update' : 'Save'}
        </button>
      </div>
    </form>
  );
}

export default SocialPostModal;
