import React, { useState } from 'react';
import './Navigation.css';

function Navigation({ preferences, onPreferenceChange }) {
  const [isOpen, setIsOpen] = useState(preferences.navPanelOpen);

  const togglePanel = () => {
    const newState = !isOpen;
    setIsOpen(newState);
    onPreferenceChange('navPanelOpen', newState);
  };

  return (
    <>
      <nav className="main-nav">
        <button className="nav-toggle" onClick={togglePanel}>
          <i className={`fas fa-bars ${isOpen ? 'active' : ''}`}></i>
        </button>
        <div className="nav-items">
          <a href="/" className="nav-item active">
            <i className="fas fa-home"></i>
            <span>Home</span>
          </a>
          <a href="/calendar" className="nav-item">
            <i className="fas fa-calendar-alt"></i>
            <span>Calendar</span>
          </a>
        </div>
      </nav>

      <div className={`nav-panel ${isOpen ? 'open' : ''}`}>
        <div className="nav-panel-header">
          <h2>Navigation</h2>
          <button className="close-panel" onClick={togglePanel}>
            <i className="fas fa-times"></i>
          </button>
        </div>
        <div className="nav-panel-content">
          {/* Panel content will go here */}
        </div>
      </div>

      {isOpen && <div className="nav-overlay" onClick={togglePanel}></div>}
    </>
  );
}

export default Navigation;
