import React, { useState, useEffect } from 'react';
import Calendar from './components/Calendar';
import AddItemModal from './components/AddItemModal';
import CampaignModal from './components/CampaignModal';
import SocialPostModal from './components/SocialPostModal';
import './App.css';

function App() {
  const [showModal, setShowModal] = useState(false);
  const [contentItems, setContentItems] = useState([]);
  const [campaigns, setCampaigns] = useState([]);
  const [socialPosts, setSocialPosts] = useState([]);
  const [editingContentItem, setEditingContentItem] = useState(null);
  const [showDropdown, setShowDropdown] = useState(false);
  const [itemType, setItemType] = useState(null);

  const itemTypes = [
    { 
      id: 'content', 
      label: 'Content Item',
      color: '#1a75ff'
    },
    { 
      id: 'campaign', 
      label: 'Campaign',
      color: '#FFB3BA'
    },
    { 
      id: 'social', 
      label: 'Social Post',
      color: '#1DA1F2'
    }
  ];

  useEffect(() => {
    fetchContentItems();
    fetchCampaigns();
    fetchSocialPosts();
  }, []);

  const fetchContentItems = async () => {
    try {
      const response = await fetch('/api/content-items');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setContentItems(data);
    } catch (error) {
      console.error('Error fetching content items:', error);
    }
  };

  const fetchCampaigns = async () => {
    try {
      const response = await fetch('/api/campaigns');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setCampaigns(data);
    } catch (error) {
      console.error('Error fetching campaigns:', error);
      setCampaigns([]);
    }
  };

  const fetchSocialPosts = async () => {
    try {
      const response = await fetch('/api/social-posts');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setSocialPosts(data);
    } catch (error) {
      console.error('Error fetching social posts:', error);
      setSocialPosts([]);
    }
  };

  const handleAddContentItem = async (newContentItem) => {
    try {
      const response = await fetch('/api/content-items', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newContentItem),
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const savedContentItem = await response.json();
      setContentItems([...contentItems, savedContentItem]);
      setShowModal(false);
    } catch (error) {
      console.error('Error adding content item:', error);
      throw error;
    }
  };

  const handleUpdateContentItem = async (updatedContentItem) => {
    try {
      const response = await fetch(`/api/content-items/${updatedContentItem.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedContentItem),
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const savedContentItem = await response.json();
      setContentItems(contentItems.map(item => 
        item.id === savedContentItem.id ? savedContentItem : item
      ));
      setShowModal(false);
    } catch (error) {
      console.error('Error updating content item:', error);
      throw error;
    }
  };

  const handleSaveContentItem = async (contentItem) => {
    if (contentItem.id) {
      await handleUpdateContentItem(contentItem);
    } else {
      await handleAddContentItem(contentItem);
    }
  };

  const handleEditContentItem = (contentItem) => {
    setEditingContentItem(contentItem);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setEditingContentItem(null);
    setItemType(null);
    setShowModal(false);
  };

  const handleDeleteContentItem = async (id) => {
    try {
      const response = await fetch(`/api/content-items/${id}`, {
        method: 'DELETE',
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      setContentItems(contentItems.filter(item => item.id !== id));
      setShowModal(false);
    } catch (error) {
      console.error('Error deleting content item:', error);
      throw error;
    }
  };

  const handleAddClick = () => {
    setShowDropdown(!showDropdown);
  };

  const handleItemTypeSelect = (type) => {
    setItemType(type.id);
    setShowDropdown(false);
    setShowModal(true);
  };

  const handleEditItem = (type, item) => {
    setItemType(type);
    setEditingContentItem(item);
    setShowModal(true);
  };

  const handleSaveCampaign = async (campaign) => {
    try {
      const method = campaign.id ? 'PUT' : 'POST';
      const url = campaign.id ? `/api/campaigns/${campaign.id}` : '/api/campaigns';
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(campaign),
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const savedCampaign = await response.json();
      
      if (campaign.id) {
        setCampaigns(campaigns.map(c => 
          c.id === savedCampaign.id ? savedCampaign : c
        ));
      } else {
        setCampaigns([...campaigns, savedCampaign]);
      }
      setShowModal(false);
    } catch (error) {
      console.error('Error saving campaign:', error);
      throw error;
    }
  };

  const handleDeleteCampaign = async (id) => {
    try {
      const response = await fetch(`/api/campaigns/${id}`, {
        method: 'DELETE',
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      setCampaigns(campaigns.filter(campaign => campaign.id !== id));
      setShowModal(false);
    } catch (error) {
      console.error('Error deleting campaign:', error);
      throw error;
    }
  };

  const handleSaveSocialPost = async (post) => {
    try {
      const method = post.id ? 'PUT' : 'POST';
      const url = post.id ? `/api/social-posts/${post.id}` : '/api/social-posts';
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(post),
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const savedPost = await response.json();
      
      if (post.id) {
        setSocialPosts(socialPosts.map(p => 
          p.id === savedPost.id ? savedPost : p
        ));
      } else {
        setSocialPosts([...socialPosts, savedPost]);
      }
      setShowModal(false);
    } catch (error) {
      console.error('Error saving social post:', error);
      throw error;
    }
  };

  const handleDeleteSocialPost = async (id) => {
    try {
      const response = await fetch(`/api/social-posts/${id}`, {
        method: 'DELETE',
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      setSocialPosts(socialPosts.filter(post => post.id !== id));
      setShowModal(false);
    } catch (error) {
      console.error('Error deleting social post:', error);
      throw error;
    }
  };

  return (
    <div className="App">
      <div className="header">
        <h1>Content Calendar</h1>
        <div className="add-button-container">
          <button onClick={handleAddClick} className="add-button">
            Add New {showDropdown ? '▼' : '▶'}
          </button>
          {showDropdown && (
            <div className="add-dropdown">
              {itemTypes.map(type => (
                <button
                  key={type.id}
                  className="dropdown-item"
                  onClick={() => handleItemTypeSelect(type)}
                >
                  {type.label}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>
      <Calendar 
        contentItems={contentItems} 
        campaigns={campaigns}
        socialPosts={socialPosts}
        onEditItem={handleEditItem}
      />
      {showModal && (
        <>
          {itemType === 'content' && (
            <AddItemModal
              contentItem={editingContentItem}
              campaigns={campaigns}
              onSave={handleSaveContentItem}
              onCancel={handleCloseModal}
              onDelete={handleDeleteContentItem}
            />
          )}
          {itemType === 'campaign' && (
            <CampaignModal
              campaign={editingContentItem}
              onSave={handleSaveCampaign}
              onCancel={handleCloseModal}
              onDelete={handleDeleteCampaign}
            />
          )}
          {itemType === 'social' && (
            <SocialPostModal
              post={editingContentItem}
              onSave={handleSaveSocialPost}
              onCancel={handleCloseModal}
              onDelete={handleDeleteSocialPost}
            />
          )}
        </>
      )}
    </div>
  );
}

export default App;
